import React, { useEffect, useState } from "react";
import "./Review.css";

const Review = (props) => {
  const { product } = props;
  // Initialize state for form input fields
  const [customerName, setCustomerName] = useState("");
  const [email, setEmail] = useState("");
  const [rating, setRating] = useState(1);
  const [reviewContent, setReviewContent] = useState("");
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetchProductReviews();
  }, [product]);

  const fetchProductReviews = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/productReviews/${product._id}`
      );
      const data = await response.json();
      if (data.success) {
        setReviews(data.reviews);
      } else {
        console.error("Failed to fetch product reviews");
      }
    } catch (error) {
      console.error("Error fetching product reviews:", error);
    }
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission behavior

    try {
      if (localStorage.getItem("auth-token")) {
        // Send a POST request to addReview endpoint with review data using fetch
        const productId = product._id;
        const response = await fetch(
          `http://localhost:4000/addReview/${productId}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              "auth-token": `${localStorage.getItem("auth-token")}`,
            },
            body: JSON.stringify({
              customerName,
              email,
              rating,
              reviewContent,
            }),
          }
        );

        // Parse response data as JSON
        const data = await response.json();

        // If review is added successfully, show success message
        if (data.success) {
          alert("Đánh giá đã được thêm!");
          // Clear form input fields after successful submission
          setCustomerName("");
          setEmail("");
          setRating(1);
          setReviewContent("");
        } else {
          // If adding review fails, show error message
          alert("Đã có lỗi xảy ra. Vui lòng thử lại sau!");
        }
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Đã có lỗi xảy ra. Vui lòng thử lại sau!");
    }
  };

  return (
    <div className="review">
      <div className="review-nav">
        <div className="review-nav-container fade">Đánh giá</div>
      </div>
      <div className="review-content">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="customerName">Tên khách hàng:</label>
            <input
              type="text"
              id="customerName"
              value={customerName}
              onChange={(e) => setCustomerName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="rating">Điểm đánh giá:</label>
            <input
              type="number"
              id="rating"
              min="1"
              max="5"
              value={rating}
              onChange={(e) => setRating(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="reviewContent">Nội dung đánh giá:</label>
            <textarea
              id="reviewContent"
              value={reviewContent}
              onChange={(e) => setReviewContent(e.target.value)}
              required
            ></textarea>
          </div>
          <button type="submit">Gửi đánh giá</button>
        </form>
        <div className="review-list">
          <h2>Đánh giá sản phẩm</h2>
          <ul>
            {reviews.map((review) => (
              <li key={review._id}>
                <p>Tên khách hàng: {review.customerName}</p>
                <p>Email: {review.email}</p>
                <p>Điểm đánh giá: {review.rating}</p>
                <p>Nội dung đánh giá: {review.reviewContent}</p>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Review;
