import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLogin, setLogin] = useState(false);
  const [provinces, setProvinces] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [ward, setWard] = useState([]);
  const [selectedProvince, setSelectedProvince] = useState("");
  const [selectedDistricts, setSelectedDistricts] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [nameInput, setNameInput] = useState("");
  const [mailInput, setMailInput] = useState("");
  const [tempNameInput, setTempNameInput] = useState("");
  const [tempMailInput, setTempMailInput] = useState("");

  useEffect(() => {
    fetchProvinces();
  }, []);

  const fetchProvinces = async () => {
    try {
      const response = await fetch("http://localhost:4000/provinces");
      if (!response.ok) {
        throw new Error(
          "Đã có lỗi trong quá trình tải danh sách tỉnh/thành phố"
        );
      }
      const data = await response.json();
      setProvinces(data);
    } catch (error) {
      console.log(
        "Đã có lỗi trong quá trình tải danh sách tỉnh/thành phố",
        error.message
      );
    }
  };

  const fetchDistricts = async (provinceName) => {
    try {
      const resp = await fetch(
        `http://localhost:4000/districts/${provinceName}`
      );
      if (!resp.ok) {
        throw new Error("Đã có lỗi trong quá trình tải danh sách quận/huyện");
      }
      const data = await resp.json();
      setDistricts(data);
    } catch (error) {
      console.log("Đã có lỗi trong quá trình tải danh sách quận/huyện");
    }
  };

  const fetchWards = async (provinceName, districtsName) => {
    try {
      const resp = await fetch(
        `http://localhost:4000/wards/${provinceName}/${districtsName}`
      );
      if (!resp.ok) {
        throw new Error("Đã có lỗi trong quá trình tải danh sách phường/xã");
      }
      const data = await resp.json();
      setWard(data);
    } catch (error) {
      console.log("Đã có lỗi trong quá trình tải danh sách phường/xã");
    }
  };

  const handleProvinceChange = (e) => {
    const selectedProvince = e.target.value;
    setSelectedProvince(selectedProvince);
    setSelectedDistricts("");
    fetchDistricts(selectedProvince);
  };

  const handleDistrictChange = (e) => {
    const selectedDistricts = e.target.value;
    setSelectedDistricts(selectedDistricts);
    fetchWards(selectedProvince, selectedDistricts);
  };

  const nameInputChange = (e) => {
    setNameInput(e.target.value);
    setTempNameInput(userData.name);
  };

  const mailInputChange = (e) => {
    setMailInput(e.target.value);
    setTempMailInput(userData.email);
  };

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem("auth-token");
        if (token) {
          setLogin(true);
        } else {
          setLogin(false);
        }
        const email = localStorage.getItem("email");
        const resp = await fetch(`http://localhost:4000/profile/${email}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!resp) {
          setLogin(false);
          throw new Error(`Lỗi: ${response.status} - ${response.statusText}`);
        }
        const userData = await resp.json();
        setUserData(userData.user);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
        setLogin(false);
      }
    };

    fetchUserData();
  }, []);

  const saveChange = async () => {
    try {
      const token = localStorage.getItem("auth-token");
      const email = localStorage.getItem("email");

      const updatedUserData = {
        name: nameInput,
        email: mailInput,
        address: `${selectedProvince}, ${selectedDistricts}, ${ward}`,
      };

      const response = await fetch(
        `http://localhost:4000/update-profile/${email}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(updatedUserData),
        }
      );
      if (!response.ok) {
        throw new Error(`Lỗi: ${response.status} - ${response.statusText}`);
      }
      console.log("Cập nhật thông tin thành công");
    } catch (error) {
      console.log("Đã có lỗi không mong đợi xảy ra");
    }
  };

  return (
    <div className="profile-container">
      {isLogin ? (
        <div>
          <h1>Thông tin người dùng</h1>
          {userData && (
            <div className="profile-info">
              <div className="input-field">
                <p className="bold-text">Họ và tên khách hàng:</p>
                <input
                  type="text"
                  value={userData.name}
                  className="input-style"
                  onChange={nameInputChange}
                />
              </div>
              <div className="input-field">
                <p className="bold-text">Địa chỉ email:</p>
                <input
                  type="text"
                  value={userData.email}
                  className="input-style"
                  onChange={mailInputChange}
                />
              </div>
              <div className="input-field">
                <p className="bold-text">Vai trò người dùng:</p>
                <input
                  type="text"
                  value={userData.role}
                  className="input-style"
                  readOnly
                />
              </div>
              {userData.address ? (
                <div>ĐÃ CÓ THÔNG TIN ĐỊA CHỈ</div>
              ) : (
                <div>
                  <p style={{ marginBottom: "15px" }}>
                    Bạn <b style={{ color: "red" }}>chưa có </b> thông tin địa
                    chỉ nào, vui lòng thêm địa chỉ!
                  </p>
                  <p className="bold-text">Địa chỉ:</p>
                  <input
                    className="input-style"
                    type="text"
                    placeholder="Số nhà, tên đường, hẻm, ngõ,..."
                    value=""
                  />
                  <div className="address_general">
                    <select
                      className="input-style"
                      name="province"
                      onChange={handleProvinceChange}
                      value={selectedProvince}
                    >
                      <option value="province_name">Chọn tỉnh/thành phố</option>
                      {provinces.map((province, index) => (
                        <option key={index} value={province}>
                          {province}
                        </option>
                      ))}
                    </select>
                    <select
                      className="input-style"
                      name="district"
                      onChange={handleDistrictChange}
                      value={selectedDistricts}
                    >
                      <option value="districts_name">Chọn quận/huyện</option>
                      {districts.map((district, index) => (
                        <option key={index} value={district}>
                          {district}
                        </option>
                      ))}
                    </select>
                    <select name="ward" className="input-style">
                      <option value="ward_name">Chọn xã/phường</option>
                      {ward.map((ward, index) => (
                        <option key={index} value={ward}>
                          {ward}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              )}
              <div className="button-saveChange">
                <button>Đổi mật khẩu</button>
                <button>Sửa thông tin</button>
                {userData.address ? <button>Thêm địa chỉ</button> : <></>}
                <button onClick={saveChange}>Lưu thay đổi</button>
              </div>
              {/* Hiển thị các thông tin khác của người dùng nếu cần */}
            </div>
          )}
        </div>
      ) : (
        <p className="error-message">ĐĂNG NHẬP DÔ MÁ :)</p>
      )}
    </div>
  );
};

export default Profile;
