import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch("/profile/:email"); // Gửi yêu cầu GET đến endpoint /profile/:email với email của người dùng
        if (!response.ok) {
          // Kiểm tra xem yêu cầu có thành công không
          throw new Error(`Lỗi: ${response.status} - ${response.statusText}`); // Ném một lỗi nếu yêu cầu không thành công
        }
        const userData = await response.json(); // Chuyển đổi dữ liệu nhận được thành đối tượng JSON
        setUserData(userData.user); // Lưu thông tin người dùng vào state
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchUserData(); // Gọi hàm fetchUserData khi component được render
  }, []);

  if (loading) return <div>Loading...</div>; // Hiển thị thông báo "Loading..." khi đang tải dữ liệu

  if (error) return <div>Error: {error}</div>; // Hiển thị thông báo lỗi nếu có lỗi xảy ra

  return (
    <div>
      <h1>Thông tin người dùng</h1>
      {userData && (
        <div>
          <p>Name: {userData.name}</p>
          <p>Email: {userData.email}</p>
          <p>Role: {userData.role}</p>
          {/* Hiển thị các thông tin khác của người dùng nếu cần */}
        </div>
      )}
    </div>
  );
};

export default Profile;
