import React, { useState, useEffect } from "react";
import "./Checkout.css";
import { Link } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [provinces, setProvinces] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [ward, setWard] = useState([]);
  const [selectedProvince, setSelectedProvince] = useState("");
  const [selectedDistricts, setSelectedDistricts] = useState("");

  useEffect(() => {
    fetchProvinces();
  });

  const fetchProvinces = async () => {
    try {
      const resp = await axios.get("/provinces");
      setProvinces(resp.data);
    } catch (error) {
      console.log("Đã có lỗi trong quá trình tải danh sách tỉnh/thành phố");
    }
  };

  const fetchDistricts = async () => {
    try {
      const resp = await axios.get(`/districts/${provinceName}`);
      setDistricts(resp.data);
    } catch (error) {
      console.log("Đã có lỗi trong quá trình tải danh sách quận/huyện");
    }
  };

  const fetchWards = async () => {
    try {
      const resp = await axios.get(`wards/${provinceName}/${districtsName}`);
      setWard(resp.data);
    } catch (error) {
      console.log("Đã có lỗi trong quá trình tải danh sách phường/xã");
    }
  };

  return (
    <div className="checkout-container">
      <h1>Xác nhận đơn hàng</h1>
      {/* Thêm nội dung trang đặt hàng ở đây */}
      <p>Thông tin đặt hàng</p>
      <select
        name="Địa chỉ"
        id="Chọn thành phố"
        value={"Chọn thành phố"}
      ></select>
      <Link to="/cart/checkout/confirm" className="confirm-btn">
        Xác nhận đơn hàng
      </Link>
    </div>
  );
};

export default Checkout;
