import React, { useState } from "react";
import "./Description.css";
import { ShopContext } from "../../Context/ShopContext";

const Description = (props) => {
  const { product } = props;
  const [showReview, setShowReview] = useState(false);
  const [userReview, setUserReview] = useState("");

  const handleReviewClick = () => {
    setShowReview(true);
  };

  const handleReviewChange = (e) => {
    setUserReview(e.target.value);
  };

  const handleSubmitReview = (e) => {
    e.preventDefault();
    alert("Đánh giá của người dùng:", userReview);
  };
  return (
    <div className="description">
      <div className="description-nav">
        <div className="description-nav-container" onClick={handleReviewClick}>
          Mô tả
        </div>
        <div className="description-nav-container fade">Đánh giá (122)</div>
      </div>
      <div className="description-content">
        <p>{product.description_detail}</p>
        {showReview && (
          <form onSubmit={handleSubmitReview}>
            <textarea
              value={userReview}
              onChange={handleReviewChange}
              placeholder="Nhập đánh giá của bạn..."
            />
            <button type="submit">Gửi đánh giá</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default Description;
