import React, { useContext, useState } from "react";
import { useParams } from "react-router-dom";
import Breadcrum from "../Component/Breadcrums/Breadcrum";
import { ShopContext } from "../Context/ShopContext";
import ShowProduct from "../Component/ShowProduct/ShowProduct";
import Description from "../Component/Description/Description";
import RelatedProducts from "../Component/RelatedProduct/RelatedProducts";
import Review from "../Component/Review/Review";
import "../Pages/CSS/Product.css";

const Product = () => {
  const { all_product } = useContext(ShopContext);
  const { productId } = useParams();
  const product = all_product.find((e) => e.id === Number(productId));
  const [activeTab, setActiveTab] = useState("description");

  if (!product || !product.image) {
    return (
      <div className="loading-container">
        <div>Đang tải tài nguyên...</div>
      </div>
    );
  }

  const handleTabChange = (tab, event) => {
    event.preventDefault();
    setActiveTab(tab);
  };

  return (
    <div className="product-container">
      <Breadcrum product={product} />
      <ShowProduct product={product} />
      <div className="tab-buttons">
        <button
          className={activeTab === "description" ? "active" : ""}
          onClick={() => handleTabChange("description")}
        >
          Mô tả
        </button>
        <button
          className={activeTab === "review" ? "active" : ""}
          onClick={() => handleTabChange("review")}
        >
          Đánh giá
        </button>
      </div>
      <div className="tab-content">
        {activeTab === "description" && <Description product={product} />}
        {activeTab === "review" && <Review product={product} />}
      </div>
      <RelatedProducts />
    </div>
  );
};

export default Product;
