import React, { useEffect, useState } from "react";
import "./Description.css";
import { ShopContext } from "../../Context/ShopContext";

const Description = (props) => {
  const { product } = props;
  const [newRating, setNewRating] = useState(0);
  const [newComment, setNewComment] = useState("");

  const submitReview = async (_id) => {
    try {
      const token = localStorage.getItem("auth-token");
      if (!token) {
        alert("Đã có lỗi không mong muốn xảy ra!");
        return;
      }

      const response = await fetch(
        `http://localhost:4000/products/${product._id}/getReviews`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            userId: userId,
            rating: selectedRating,
            comment: reviewComment,
          }),
        }
      );
      const data = await response.json();
      console.log("Review submitted:", data);
    } catch (error) {
      console.error("Error submitting review:", error);
    }
  };

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/products/${product._id}/viewReviews`
        );
        const data = await response.json();
        setReview(data);
      } catch (error) {
        console.error("Error fetching reviews:", error);
      }
    };
    fetchReviews();
  }, [product.id]);

  const handleCommentChange = (e) => {
    setNewComment(e.target.value);
  };

  const handleRatingChange = (rating) => {
    setNewRating(rating);
  };

  // const submitComment = async () => {
  //   try {
  //     const token = localStorage.getItem("auth-token");
  //     if (!token) {
  //       alert("Vui lòng đăng nhập/đăng ký để có thể đánh giá");
  //       return;
  //     }
  //     const decodedToken = jwt_decode(token);
  //     const userId = decodedToken.userId;

  //     const response = await fetch(
  //       `http://localhost:4000/products/${product.id}/reviews`,
  //       {
  //         method: "POST",
  //         headers: {
  //           "Content-Type": "application/json",
  //           Authorization: `Bearer ${token}`,
  //         },
  //         body: JSON.stringify({
  //           userId: userId,
  //           rating: newRating,
  //           comment: newComment,
  //         }),
  //       }
  //     );
  //     const data = await response.json();
  //     console.log("Comment submitted:", data);

  //     // Cập nhật danh sách bình luận sau khi gửi thành công
  //     setReview([...review, data]);
  //     setNewComment(""); // Xóa nội dung trường nhập
  //     setNewRating(0);
  //   } catch (error) {}
  // };

  return (
    <div className="description">
      <div className="description-nav">
        <div className="description-nav-container">Mô tả</div>
        <div className="description-nav-container fade">Đánh giá ()</div>
      </div>
      <div className="description-review">
        <h2>Đánh giá sản phẩm</h2>
        <div className="description-review-rating">
          {/* Tạo các nút hoặc sao để người dùng chọn rating */}
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              onClick={() => handleRatingChange(star)}
              style={{ cursor: "pointer" }}
            >
              {star <= newRating ? "★" : "☆"}
            </span>
          ))}
        </div>
        <textarea
          value={newComment}
          onChange={handleCommentChange}
          placeholder="Nhập bình luận của bạn..."
        />
        <button onClick={submitReview}>Gửi đánh giá</button>
      </div>
    </div>
  );
};

export default Description;
