import React, { useEffect, useState } from "react";
import "./Description.css";
import { ShopContext } from "../../Context/ShopContext";

const Description = (props) => {
  const { product } = props;
  const [review, setReview] = useState([]);
  const [newComment, setNewComment] = useState("");

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/products/${productId}/reviews`
        );
        const data = await response.json();
        setReview(data);
      } catch (error) {
        console.error("Error fetching reviews:", error);
      }
    };
    fetchReviews();
  }, [product.id]);

  const handleCommentChange = (e) => {
    setNewComment(e.target.value);
  };

  const submitComment = async () => {
    try {
      const token = localStorage.getItem("auth-token");
      if (!token) {
        alert("Vui lòng đăng nhập/đăng ký để có thể đánh giá");
        return;
      }
    } catch (error) {}
  };

  return (
    <div className="description">
      <div className="description-nav">
        <div className="description-nav-container">Mô tả</div>
        <div className="description-nav-container fade">
          Đánh giá ({review.length})
        </div>
      </div>
      <div className="description-content">
        <p>{product.description_detail}</p>
        <ul>
          {review.map((review, i) => (
            <li key={i}>
              <p>Đánh giá: {review.rating}</p>
              <p>Bình luận: {review.comment}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Description;
