import React from "react";
import "./Description.css";
import { ShopContext } from "../../Context/ShopContext";

const Description = (props) => {
  const { product } = props;

  const submitReview = async () => {
    try {
      const token = localStorage.getItem("auth-token");
      if (!token) {
        alert("Đã có lỗi không mong muốn xảy ra!");
        return;
      }
      const decodedToken = jwt_decode(token);
      const userId = decodedToken.userId;

      const response = await fetch(
        `http://localhost:4000/products/${productId}/reviews`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
    } catch (error) {}
  };
  return (
    <div className="description">
      <div className="description-nav">
        <div className="description-nav-container">Mô tả</div>
        {/* <div className="description-nav-container fade">Đánh giá (122)</div> */}
      </div>
      <div className="description-content">
        <p>{product.description_detail}</p>
      </div>
    </div>
  );
};

export default Description;
