import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLogin, setLogin] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem("auth-token");
        if (token) {
          setLogin(true);
        } else {
          setLogin(false);
        }
        const email = localStorage.getItem("email");
        const resp = await fetch(`http://localhost:4000/profile/${email}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!resp) {
          setLogin(false);
          throw new Error(`Lỗi: ${response.status} - ${response.statusText}`);
        }
        const userData = await resp.json();
        setUserData(userData.user);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
        setLogin(false);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="profile-container">
      {isLogin ? (
        <div>
          <h1>Thông tin người dùng</h1>
          {userData && (
            <div className="profile-info">
              <div className="input-field">
                <p className="bold-text">Họ và tên khách hàng:</p>
                <input
                  type="text"
                  value={userData.name}
                  className="input-style"
                  readOnly
                />
              </div>
              <div className="input-field">
                <p className="bold-text">Địa chỉ email:</p>
                <input
                  type="text"
                  value={userData.email}
                  className="input-style"
                  readOnly
                />
              </div>
              <div className="input-field">
                <p className="bold-text">Vai trò người dùng:</p>
                <input
                  type="text"
                  value={userData.role}
                  className="input-style"
                  readOnly
                />
              </div>
              {userData.address ? (
                <div>ĐÃ CÓ THÔNG TIN ĐỊA CHỈ</div>
              ) : (
                <div>
                  Bạn chưa có thông tin địa chỉ nào, vui lòng thêm địa chỉ!
                </div>
              )}
              {/* Hiển thị các thông tin khác của người dùng nếu cần */}
            </div>
          )}
        </div>
      ) : (
        <p className="error-message">ĐĂNG NHẬP DÔ MÁ :)</p>
      )}
    </div>
  );
};

export default Profile;
