import React, { useEffect, useState } from "react";
import "./Description.css";
import { ShopContext } from "../../Context/ShopContext";

const Description = (props) => {
  const { product } = props;
  const [review, setReview] = useState([]);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/products/${productId}/reviews`
        );
        const data = await response.json();
        setReview(data);
      } catch (error) {
        console.error("Error fetching reviews:", error);
      }
    };
    fetchReviews();
  }, [product.id]);

  return (
    <div className="description">
      <div className="description-nav">
        <div className="description-nav-container">Mô tả</div>
        <div className="description-nav-container fade">
          Đánh giá ({review.length})
        </div>
      </div>
      <div className="description-content">
        <p>{product.description_detail}</p>
        <ul>
          {review.map((review, i) => (
            <li key={i}>
              <p>Đánh giá: {review.rating}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Description;
