import React, { useState, useEffect } from "react";
import "./Checkout.css";
import { Link } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [provinces, setProvinces] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [ward, setWard] = useState([]);
  const [selectedProvince, setSelectedProvince] = useState("");
  const [selectedDistricts, setSelectedDistricts] = useState("");

  useEffect(() => {
    fetchProvinces();
  }, []);

  const fetchProvinces = async () => {
    try {
      const response = await fetch("http://localhost:4000/provinces");
      if (!response.ok) {
        throw new Error(
          "Đã có lỗi trong quá trình tải danh sách tỉnh/thành phố"
        );
      }
      const data = await response.json();
      setProvinces(data);
    } catch (error) {
      console.log(
        "Đã có lỗi trong quá trình tải danh sách tỉnh/thành phố",
        error.message
      );
    }
  };

  const fetchDistricts = async () => {
    try {
      const resp = await fetch(
        `http://localhost:4000/districts/${provinceName}`
      );
      if (!response.ok) {
        throw new Error("Failed to fetch districts");
      }
      setDistricts(resp.data);
    } catch (error) {
      console.log("Đã có lỗi trong quá trình tải danh sách quận/huyện");
    }
  };

  const fetchWards = async () => {
    try {
      const resp = await axios.get(`wards/${provinceName}/${districtsName}`);
      setWard(resp.data);
    } catch (error) {
      console.log("Đã có lỗi trong quá trình tải danh sách phường/xã");
    }
  };

  const handleProvinceChange = (e) => {
    const selectedProvince = e.target.value;
    setSelectedProvince(selectedProvince);
    setSelectedDistricts("");
    fetchDistricts(selectedProvince);
  };

  const handleDistrictChange = (e) => {
    const selectedDistricts = e.target.value;
    setSelectedDistricts(selectedDistricts);
    fetchWards(selectedDistricts);
  };

  return (
    <div className="checkout-container">
      <h1>Xác nhận đơn hàng</h1>
      {/* Thêm nội dung trang đặt hàng ở đây */}
      <p>Thông tin đặt hàng</p>
      <select
        name="province"
        onChange={handleProvinceChange}
        value={selectedProvince}
      >
        <option value="">Chọn tỉnh/thành phố</option>
        {provinces.map((province, index) => (
          <option key={index} value={province}>
            {province}
          </option>
        ))}
      </select>
      <br />
      <Link to="/cart/checkout/confirm" className="confirm-btn">
        Xác nhận đơn hàng
      </Link>
    </div>
  );
};

export default Checkout;
